module rom (
  input [2:0] address,
  output reg [6:0] q
);

  always @* begin
    case (address)
      3'd0: q <= 7'd0;
      3'd1: q <= 7'd10;
      3'd2: q <= 7'd20;
      3'd3: q <= 7'd30;
      3'd4: q <= 7'd40;
      3'd5: q <= 7'd50;
      3'd6: q <= 7'd60;
      default: q <= 7'd70;
    endcase
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity rom is
  port (
    address : in std_logic_vector(2 downto 0);
    q : out std_logic_vector(6 downto 0)
  );
end entity;

architecture rtl of rom is
  type rom_t is array (0 to 7) of integer;
  constant rom : rom_t := (0,10,20,30,40,50,60,70);
begin

  q <= std_logic_vector(to_unsigned(rom(to_integer(unsigned(address))), q'length));

end architecture;

let rom ~address =
  mux address (List.init 8 ~f:(fun i -> of_unsigned_int ~width:7 (i * 10)))
;;

module mux4 (
  input [1:0] address,
  input [7:0] a, b, c, d,
  output reg [7:0] q
);
   always @* begin
      case (address)
        3'd0: q <= a;
        3'd1: q <= b;
        3'd2: q <= c;
        default: q <= d;
      endcase
   end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity mux4 is
  port (
    address : in std_logic_vector(1 downto 0);
    a, b, c, d : in std_logic_vector(7 downto 0);
    q : out std_logic_vector(7 downto 0)
  );
end entity;

architecture rtl of mux4 is
  signal q_int : std_logic_vector(7 downto 0);
begin

  q <= q_int;

  process (address, a, b, c, d) is 
  begin
    case to_integer(unsigned(address)) is
      when 0 => q_int <= a;
      when 1 => q_int <= b;
      when 2 => q_int <= c;
      when others => q_int <= d;
    end case;
  end process;

end architecture;

module priority_encoder (
  input [3:0] sel,
  input [7:0] a, b, c, d,
  output reg [7:0] q
);

  always @* begin
    if (sel[3]) q <= d;
    else if (sel[2]) q <= c;
    else if (sel[1]) q <= b;
    else q <= a;
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity priority_encoder is
  port (
    sel : in std_logic_vector(3 downto 0);
    a, b, c, d : in std_logic_vector(7 downto 0);
    q : out std_logic_vector(7 downto 0)
  );
end entity;

architecture rtl of priority_encoder is
begin

  q <= d when sel(3) = '1' else
       c when sel(2) = '1' else 
       b when sel(1) = '1' else
       a;

end architecture;

let priority_encoder_1 ~sel ~a ~b ~c ~d =
  let out = Always.Variable.wire ~default:(zero 8) () in
  Always.(
    compile
      [ if_ sel.:(3) [ out <-- d ]
        @@ elif sel.:(2) [ out <-- c ]
        @@ elif sel.:(1) [ out <-- b ]
        @@ [ out <-- a ]
      ]);
  out.value
;;

let priority_encoder_2 ~sel ~a ~b ~c ~d =
  priority_select_with_default
    ~default:a
    With_valid.
      [ { valid = sel.:(3); value = d }
      ; { valid = sel.:(2); value = c }
      ; { valid = sel.:(1); value = b }
      ]
;;

module parity (
  input [3:0] d,
  output q
);

  assign q = ^d;

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity parity is
  port (
    d : in std_logic_vector(3 downto 0);
    q : out std_logic
  );
end entity;

architecture rtl of parity is
begin

  process (d) is 
    variable parity : std_logic;
  begin
    parity := '0';
    for i in d'range loop
      parity := parity xor d(i);
    end loop;
    q <= parity;
  end process;

end architecture;

let parity_2 ~d =
  let parity = ref gnd in
  for i = 0 to width d - 1 do
    parity := !parity ^: d.:(i)
  done;
  !parity
;;

module alu (
  input [3:0] op,
  input [7:0] a, b,
  output reg [7:0] q
);

always @* begin
    case (op)
      0: q <= a + b;
      1: q <= a - b;
      2: q <= a * b;
      3: q <= a << 1;
      4: q <= a >> 1;
      5: q <= a & b;
      6: q <= a | b;
      7: q <= a ^ b;
      8: q <= ~a;
      9: q <= a < b;
      10: q <= a == b;
      default: q <= 0;
    endcase
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity alu is
  port (
    op : in std_logic_vector(3 downto 0);
    a, b : in std_logic_vector(7 downto 0);
    q : out std_logic_vector(7 downto 0)
  );
end entity;

architecture rtl of alu is
  signal q_int : std_logic_vector(7 downto 0);
begin

  process (op, a, b) is
    variable mul : std_logic_vector(15 downto 0);
  begin
    case to_integer(unsigned(op)) is
      when 0 => q_int <= std_logic_vector(unsigned(a) + unsigned(b));
      when 1 => q_int <= std_logic_vector(unsigned(a) - unsigned(b));
      when 2 => 
        mul := std_logic_vector(unsigned(a) * unsigned(b));
        q_int <= mul(7 downto 0);
      when 3 => q_int <= std_logic_vector(shift_left(unsigned(a), 1));
      when 4 => q_int <= std_logic_vector(shift_right(unsigned(a), 1));
      when 5 => q_int <= a and b;
      when 6 => q_int <= a or b;
      when 7 => q_int <= a xor b;
      when 8 => q_int <= not a;
      when 9 => 
        if unsigned(a) < unsigned(b) then
          q_int <= "00000001";
        else
          q_int <= "00000000";
        end if;
      when 10 =>
        if a = b then
          q_int <= "00000001";
        else
          q_int <= "00000000";
        end if;
      when others => q_int <= (others => '0');
    end case;
  end process;

end architecture;

