module jk_flip_flop (
  input clock, j, k,
  output reg q
);

  always @(posedge clock) begin
    case ({j,k})
      2'b00: q <= q;
      2'b01: q <= 1'b0;
      2'b10: q <= 1'b1;
      2'b11: q <= ~q;
    endcase
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity jk_flip_flop is
  port (
    clock, j, k : in std_logic;
    q : out std_logic
  );
end entity;

architecture rtl of jk_flip_flop is
  signal q_int : std_logic;
  signal jk : std_logic_vector(1 downto 0);
begin

  jk <= j & k;
  q <= q_int;

  process (clock) is
  begin
    if rising_edge(clock) then
      case jk is
        when "01" => q_int <= '0';
        when "10" => q_int <= '1';
        when "11" => q_int <= not q_int;
        when others => null;
      end case;
    end if;
  end process;


end architecture;

let jk_flip_flop_1 ~clock ~j ~k =
  let q = Always.Variable.reg (Reg_spec.create ~clock ()) ~width:1 in
  Always.(
    compile
      [ switch
          (j @: k)
          [ of_string "00", [ q <-- q.value ]
          ; of_string "01", [ q <-- gnd ]
          ; of_string "10", [ q <-- vdd ]
          ; of_string "11", [ q <-- ~:(q.value) ]
          ]
      ]);
  q.value
;;

let jk_flip_flop_2 ~clock ~j ~k =
  reg_fb (Reg_spec.create ~clock ()) ~width:1 ~f:(fun q ->
    mux (j @: k) [ q; gnd; vdd; ~:q ])
;;

module t_flip_flop (
  input clock, reset_n, t,
  output reg q
);

  always @(posedge clock, negedge reset_n) begin
    if (!reset_n)
      q <= 1'b0;
    else if (t)
      q <= ~q;
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity t_flip_flop is
  port (
    clock, reset_n, t : in std_logic;
    q : out std_logic
  );
end entity;

architecture rtl of t_flip_flop is 
  signal q_int : std_logic;
begin

  q <= q_int;

  process (clock, reset_n) begin
    if reset_n = '0' then 
      q_int <= '0';
    elsif rising_edge(clock) then
      if t = '1' then
        q_int <= not q_int;
      end if;
    end if;
  end process;

end architecture;

let t_flip_flop_1 ~clock ~reset_n ~t =
  let q =
    Always.Variable.reg
      (Reg_spec.create ~clock ~reset:reset_n ~reset_edge:Falling ())
      ~width:1
  in
  Always.(compile [ if_ t [ q <-- ~:(q.value) ] [ q <-- q.value ] ]);
  q.value
;;

module d_flip_flop (
  input clock, clear, reset, enable, d,
  output reg q
);

  always @(posedge clock, posedge reset) begin
    if (reset)
      q <= 1'b1;
    else if (clear)
      q <= 1'b0;
    else if (enable)
      q <= d;
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity d_flip_flop is
  port (
    clock, reset, clear, enable, d : in std_logic;
    q : out std_logic
  );
end entity;

architecture rtl of d_flip_flop is 
  signal q_int : std_logic;
begin

  q <= q_int;

  process (clock, reset) begin
    if reset = '1' then
      q_int <= '1';
    elsif rising_edge(clock) then
      if clear = '1' then 
        q_int <= '0';
      elsif enable = '1' then
        q_int <= d;
      end if;
    end if;
  end process;

end architecture;

let d_flip_flop ~clock ~reset ~clear ~enable ~d =
  reg (Reg_spec.create ~clock ~reset ~clear ()) ~enable d
;;

module ring_counter #(
  parameter N = 4
) (
  input clock, clear,
  output reg [N-1:0] q
);

  always @(posedge clock) begin
    if (clear)
      q <= 1;
    else
      q <= { q[0], q[N-1:1] };
  end 

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity ring_counter is
  generic (
    N : integer := 4
  );
  port (
    clock, clear : in std_logic;
    q : out std_logic_vector(N-1 downto 0)
  );
end entity;

architecture rtl of ring_counter is
    signal q_int : std_logic_vector(N-1 downto 0);
begin

  q <= q_int;

  process (clock) is
  begin
    if rising_edge(clock) then
      if clear = '1' then
        q_int <= std_logic_vector(to_unsigned(1, q_int'length));
      else
        q_int <= q_int(0) & q_int(N-1 downto 1);
      end if;
    end if;
  end process;

end architecture;

let ring_counter ~n ~clock ~clear =
  reg_fb (Reg_spec.create ~clock ~clear ()) ~width:n ~clear_to:(one n) ~f:(rotr ~by:1)
;;

module mobius_counter #(
  parameter N = 4
) (
  input clock, clear,
  output reg [N-1:0] q
);

  always @(posedge clock) begin
    if (clear)
      q <= 0;
    else
      q <= { ~q[0], q[N-1:1] };
  end 

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity mobius_counter is
  generic (
    N : integer := 4
  );
  port (
    clock, clear : in std_logic;
    q : out std_logic_vector(N-1 downto 0)
  );
end entity;

architecture rtl of mobius_counter is
    signal q_int : std_logic_vector(N-1 downto 0);
begin

  q <= q_int;

  process (clock) is
  begin
    if rising_edge(clock) then
      if clear = '1' then
        q_int <= (others => '0');
      else
        q_int <= (not q_int(0)) & q_int(N-1 downto 1);
      end if;
    end if;
  end process;

end architecture;

let mobius_counter ~n ~clock ~clear =
  reg_fb (Reg_spec.create ~clock ~clear ()) ~width:n ~f:(fun d -> ~:(lsb d) @: msbs d)
;;

module modulo_n_counter #(
  parameter width = 4,
  parameter n = 12
) (
  input clock, clear, increment,
  output reg [width-1:0] q
);

  always @(posedge clock)
  begin
    if (clear) 
      q <= 0;
    else if (increment)
      if (q == (n-1)) q <= 0;
      else q <= q + 1;
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity modulo_n_counter is
  generic (
    width : integer := 4;
    n : integer := 12
  );
  port (
    clock, clear, increment : in std_logic;
    q : out std_logic_vector(width-1 downto 0)
  );
end entity;

architecture rtl of modulo_n_counter is
    signal q_int : std_logic_vector(width-1 downto 0);
begin

  q <= q_int;

  process (clock) is 
  begin
    if rising_edge(clock) then
      if clear = '1' then
        q_int <= (others => '0');
      elsif increment = '1' then
       if (q_int = std_logic_vector(to_unsigned(n-1, q'length))) then
          q_int <= (others => '0');
        else
          q_int <= std_logic_vector(unsigned(q_int) +  1);
        end if;
      end if;
    end if;
  end process;

end architecture;

let modulo_n_counter_1 ~config:{ width; n } ~clock ~clear ~increment =
  let spec = Reg_spec.create ~clock ~clear () in
  let out = Always.Variable.reg spec ~width in
  Always.(
    compile
      [ when_
          increment
          [ if_ (out.value ==:. n - 1) [ out <--. 0 ] [ out <-- out.value +:. 1 ] ]
      ]);
  out.value
;;

module gray_counter #(
  parameter N = 4
) (
  input clock, clear,
  output reg [N-1:0] q
);
  reg [N-1:0] count;

   always @(posedge clock) begin
     if (clear) begin
        count <= 0;
        q <= 0;
      end else begin
        count <= count + 1;
        q <= { count[N-1], count[N-1:1] ^ count[N-2:0] };
      end
   end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity gray_counter is
  generic (
    N : integer := 4
  );
  port (
    clock, clear : in std_logic;
    q : out std_logic_vector(N-1 downto 0)
  );
end entity;

architecture rtl of gray_counter is 
  signal count : std_logic_vector(N-1 downto 0);
  signal q_int : std_logic_vector(N-1 downto 0);
begin

  q <= q_int;

  process (clock) begin
    if rising_edge(clock) then
      if clear = '1' then 
        count <= (others => '0');
        q_int <= (others => '0');
      else 
        count <= std_logic_vector(unsigned(count) + 1);
        q_int <= count(N-1) & (count(N-1 downto 1) xor count(N-2 downto 0));
      end if;
    end if;
  end process;

end architecture;

let gray_counter_1 ~n ~clock ~clear =
  let spec = Reg_spec.create ~clock ~clear () in
  let q = Always.Variable.reg spec ~width:n in
  let out = Always.Variable.reg spec ~width:n in
  Always.(
    compile
      [ q <-- q.value +:. 1
      ; out <-- q.value.:(n - 1) @: q.value.:[n - 1, 1] ^: q.value.:[n - 2, 0]
      ]);
  out.value
;;

let gray_counter_2 ~n ~clock ~clear =
  let spec = Reg_spec.create ~clock ~clear () in
  let q = reg_fb spec ~width:n ~f:(fun q -> q +:. 1) in
  reg spec (msb q @: msbs q ^: lsbs q)
;;

module bidirectional_shift_reg #(
  parameter N = 4
) (
  input clock, clear, enable, dir, d,
  output reg [N-1:0] q
);

  always @(posedge clock) begin
    if (clear)
      q <= 0;
    else
      if (enable) 
        if (dir)
          q <= { d, q[N-1:1] };
        else
          q <= { q[N-2:0], d };
  end 

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity bidirectional_shift_reg is
  generic (
    N : integer := 4
  );
  port (
    clock, clear, enable, dir, d : in std_logic;
    q : out std_logic_vector(N-1 downto 0)
  );
end entity;

architecture rtl of bidirectional_shift_reg is
  signal q_int : std_logic_vector(N-1 downto 0);
begin

  q <= q_int;

  process (clock) is 
  begin
    if rising_edge(clock) then
      if clear = '1' then
        q_int <= (others => '0');
      else
        if enable = '1' then
          if dir = '1' then
            q_int <= d & q_int(N-1 downto 1);
          else
            q_int <= q_int(N-2 downto 0) & d;
          end if;
        end if;
      end if;
    end if;
  end process;

end architecture;

let bidirectional_shift_reg_1 ~n ~clock ~clear ~enable ~dir ~d =
  let out = Always.Variable.reg (Reg_spec.create ~clock ~clear ()) ~width:n in
  Always.(
    compile
      [ when_
          enable
          [ if_
              dir
              [ out <-- d @: out.value.:[n - 1, 1] ]
              [ out <-- out.value.:[n - 2, 0] @: d ]
          ]
      ]);
  out.value
;;

let bidirectional_shift_reg_2 ~n ~clock ~clear ~enable ~dir ~d =
  reg_fb (Reg_spec.create ~clock ~clear ()) ~width:n ~enable ~f:(fun out ->
    mux2 dir (d @: msbs out) (lsbs out @: d))
;;

module single_port_ram (
  input clock, write_enable,
  input [2:0] address,
  input [7:0] write_data,
  output reg [7:0] read_data
);

  reg [7:0] mem[0:7];

  always @(posedge clock) begin
    if (write_enable)
      mem[address] <= write_data;
    read_data <= mem[address];
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity single_port_ram is
  port (
    clock, write_enable : in std_logic;
    address : in std_logic_vector(2 downto 0);
    write_data : in std_logic_vector(7 downto 0);
    read_data : out std_logic_vector(7 downto 0)
  );
end entity;

architecture rtl of single_port_ram is 

  type mem_t is array (0 to 7) of std_logic_vector(7 downto 0);
  signal mem : mem_t;
  signal read_data_int : std_logic_vector(7 downto 0);

begin

  read_data <= read_data_int;

  process (clock) is
  begin
      if rising_edge(clock) then
        if write_enable = '1' then
          mem(to_integer(unsigned(address))) <= write_data;
        end if;
        read_data_int <= mem(to_integer(unsigned(address)));
      end if;
  end process;

end architecture;

module sync_fifo (
  input clock, clear, write, read,
  input [15:0] data_in,
  output reg [15:0] data_out,
  output full, empty
);

  reg [2:0] wptr, rptr;
  reg [15:0] mem[0:7];

  assign full = (wptr + 1) == rptr;
  assign empty = wptr == rptr;

  wire write_incr = write & !full;
  wire read_incr = read & !empty;

  always @(posedge clock) begin
    if (clear) begin
      rptr <= 0;
      wptr <= 0;
    end else begin
      if (write_incr) wptr <= wptr + 1;
      if (read_incr) rptr <= rptr + 1;
    end
    if (write_incr) mem[wptr] <= data_in;
    if (read_incr) data_out <= mem[rptr];
  end

endmodule

library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

entity sync_fifo is
  port (
    clock, clear, write, read : in std_logic;
    data_in : in std_logic_vector(15 downto 0);
    data_out : out std_logic_vector(15 downto 0);
    full, empty : out std_logic
  );
end entity;

architecture rtl of sync_fifo is
  type mem_t is array (0 to 7) of std_logic_vector(15 downto 0);
  signal mem : mem_t;

  signal wptr, rptr : unsigned(2 downto 0);
  signal write_incr : std_logic;
  signal read_incr : std_logic;
  signal full_int : std_logic;
  signal empty_int : std_logic;
begin

  full_int <= '1' when (wptr + 1) = rptr else '0';
  empty_int <= '1' when wptr = rptr else '0';
  full <= full_int;
  empty <= empty_int;

  write_incr <= write and (not full_int);
  read_incr <= read and (not full_int);

  process (clock) begin
    if rising_edge(clock) then
      if clear = '1' then
        rptr <= (others => '0');
        wptr <= (others => '0');
      else
        if write_incr = '1' then 
          wptr <= wptr + 1;
        end if;
        if read_incr = '1' then 
          rptr <= rptr + 1;
        end if;
      end if;

      if write_incr = '1' then
        mem(to_integer(wptr)) <= data_in;
      end if;
      if read_incr = '1' then
        data_out <= mem(to_integer(rptr));
      end if;
    end if;
  end process;
end architecture;

