(* A useful mental model above is to treat `foo_branch` as a "C void
   function" that takes a pointer to write its output value to.
*)
let foo_branch (o_value : Hardcaml.Always.Variable.t) =
  let open Signal in
  let foo = Always.Variable.reg ~enable:vdd  ~width:32 r_sync in
  Always.[
    foo <-- (foo.value +:. 1);
    o_value <-- foo.value;
  ]
;;

let main : Always.t list =
  let cond = Signal.input "cond" 1 in
  let o_value = Always.Variable.wire ~default:(Signal.zero 32) () in
  Always.[
    if_ cond [
      (* [proc] turns a [Always.t list] to an [Always.t], without
         changing any semantic meaning of the program.
      *)
      proc (foo_branch o_value);
    ] [
      o_value <--. 0;
    ]
  ]
;;

type stream =
  { valid : Signal.t
  ; data : Signal.t
  ; accept : Always.Variable.t
  }

let handshake stream callback =
  Always.(proc [
    when_ stream.valid [
      stream.accept <--. 1;
      proc (callback stream.data);
    ]
  ])
;;

let main (stream_a : stream) (stream_b : stream) =
  let foo = Always.Variable.wire ~default:Signal.gnd () in
  let bar = Always.Variable.wire ~default:Signal.gnd () in
  Always.compile [
    handshake stream_a (fun data ->
      Always.[
        foo <-- data
      ]
    );

    handshake stream_b (fun data ->
      Always.[
        foo <--. 0;
        bar <-- ~:(data)
      ]
    );
  ]
;;

module sequence_detector (
  input clock, clear, d,
  output reg detect
);

  localparam 
    S1 = 0, 
    S10 = 1, 
    S101 = 2;

  reg [1:0] state;

  always @(posedge clock) 
    if (clear) begin 
      state <= S1;
      detect <= 0;
    end else begin
      case (state)
        S1: 
          if (d) state <= S10;
          else state <= S1;
        S10: 
          if (d) state <= S1;
          else state <= S101;
        S101: 
          state <= S1;
      endcase

      detect <= (state == S101) & d;
    end

endmodule

library ieee;
use ieee.std_logic_1164.all;

entity sequence_detector_vhdl is
  port (
    clock, clear, d : in std_logic;
    detect : out std_logic
  );
end entity;

architecture rtl of sequence_detector_vhdl is

  type state_t is (S1, S10, S101);
  signal state : state_t;

begin

  process (clock) begin
    if rising_edge(clock) then 
      if clear = '1' then 
        state <= S1;
        detect <= '0';
      else
        case (state) is
          when S1 => 
            if d = '1' then state <= S10;
            else state <= S1;
            end if;
          when S10 => 
            if d = '1' then state <= S1;
            else state <= S101;
            end if;
          when S101 => 
            state <= S1;
        end case;

        if state = S101 and d = '1' then
          detect <= '1';
        else
          detect <= '0';
        end if;
      end if;
    end if;
  end process;

end architecture;

let software_reference sequence bits =
  let open Bits in
  List.init
    (width bits - width sequence + 1)
    ~f:(fun pos ->
      if to_bool (bits.:[width sequence + pos - 1, pos] ==: sequence)
      then Some pos
      else None)
  |> List.filter_opt
  (* We find the first bit of the match, but the hardware will find the last bit so offset
     it by the length of the sequence. *)
  |> List.map ~f:(fun d -> d + width sequence - 1)
;;

let test ~sequence ~data_in =
  let sim = create_sequence_detector_sim ~sequence in
  let d = Cyclesim.in_port sim "d" in
  let detect = Cyclesim.out_port sim "detect" in
  let open Bits in
  let cycles = width data_in in
  let results = ref [] in
  for i = 0 to cycles - 1 do
    d := data_in.:(i);
    Cyclesim.cycle sim;
    if Bits.to_bool !detect then results := i :: !results
  done;
  let results = List.rev !results in
  let expected = software_reference sequence data_in in
  if not (List.equal Int.equal results expected)
  then
    raise_s
      [%message
        (sequence : Bits.t) (data_in : Bits.t) (results : int list) (expected : int list)]
;;

let double_dabble value =
  let digits = String.length (Int.to_string value) in
  let num_bits = Int.ceil_log2 (value + 1) in
  let value = ref value in
  for _ = 0 to num_bits - 1 do
    for j = 0 to digits - 1 do
      let digit = (!value lsr (num_bits + (j * 4))) land 0xf in
      if digit >= 5 then value := !value + (3 lsl (num_bits + (j * 4)))
    done;
    value := !value lsl 1
  done;
  let bcd = !value lsr num_bits in
  List.init digits ~f:(fun i -> (bcd lsr (i * 4)) land 0xf)
  |> List.rev
  |> List.map ~f:(fun i -> Char.of_int_exn (i + Char.to_int '0'))
  |> String.of_char_list
;;

let%expect_test "Test algorithm" =
